mapper.select:
SELECT case_apply.*, employee_info.user_name, case_info.case_code, case_info.org_name,case_info.`color` , case_info.`case_name`,case_info.`case_money`,entrust.`name`,
case_info.case_num,case_info.case_card,case_info.account_no,case_info.cus_no,case_info.loan_contract,case_info.case_file_no,case_info.overdue_days, case_info.mobile1,case_info.mobile2,case_info.sales_dep, case_info.bank_account,case_info.case_date,case_info.overdue_age
FROM case_apply LEFT JOIN employee_info ON case_apply.apply_user=employee_info.id LEFT JOIN case_info ON case_apply.case_id=case_info.id LEFT JOIN case_batch ON case_info.`batch_id`=case_batch.`id` LEFT JOIN entrust ON entrust.`id`=case_batch.`entrust_id` where 1=1 and case_batch.status != -1
AND ( case_info.org_id IN ( ${params.queryOrgs} )
OR case_batch.entrust_id IN (${params.attachEntrustId})
OR 1=1
)
and case_info.`state`IN ('0','1','4')
and case_info.`state`IN ('0','1','4','3')
and case_id=#{params.caseId}
and case_apply.apply_type in ${params.applyTypes}
and case_apply.apply_type = ${params.applyType}
and case_apply.state=${params.state}
and case_info.case_code like CONCAT('%','${params.caseCode}','%' )
and case_batch.entrust_id = #{params.entrustId}
AND case_info.org_id IN ( SELECT id FROM sys_organization WHERE path LIKE CONCAT(( SELECT path FROM sys_organization WHERE id = #{params.orgId} ), '%') )
and (DATE_FORMAT(case_apply.app_time,'%Y-%m-%d') between #{params.appTimeStart} and #{params.appTimeEnd})
and case_apply.apply_user_name like CONCAT('%','${params.applyUserName}','%' )
and case_apply.id in
#{params.caseIdMore[${index}]}
order by ${sort} ${order}
order by case_apply.app_time desc

mapper.update:
update case_apply set 
state = #{state},

approval_opinion=#{approvalOpinion},

sur_user=#{surUser},
sur_user_name=#{surUserName},
sur_time=#{surTime}


	,mail_time=#{mailTime}

where apply_type = 1
and id in

	#{id}
