mapper.select:
select phone_record.*, employee_info.user_name from phone_record left join employee_info on phone_record.create_emp_id=employee_info.id where 1=1
and case_id=#{params.caseId}
and create_emp_id=#{params.createEmpId}
order by ${sort} ${order}
order by phone_record.create_time desc

select phone_record.*, employee_info.user_name as creatorName, aaa.user_name as collectorName from phone_record left join employee_info on phone_record.create_emp_id=employee_info.id left join ( SELECT case_info.id, employee_info.user_name FROM case_info LEFT JOIN employee_info ON case_info.case_assigned_id = employee_info.id WHERE case_info.id = #{params.caseId} ) aaa on phone_record.case_id=aaa.id where 1=1
and phone_record.case_id=#{params.caseId}
and phone_record.pr_cat=#{params.prCat}
and phone_record.create_emp_id=#{params.createEmpId}
order by ${sort} ${order}
order by phone_record.create_time desc

mapper.update:
update
phone_record
set cp_state = #{cpState,jdbcType=VARCHAR}
where id =
#{id,jdbcType=VARCHAR}
